module APIHelper

  def post_to_slack(channel:, text:, attachments:[])
    uri = URI("https://slack.com/api/chat.postMessage")
    payload = {
      token: ENV["SLACK_TOKEN"],
      channel: channel,
      text: text,
      parse: 'full',
      attachments: attachments.to_json
    }
    res = Net::HTTP.post_form(uri, payload)
    res.body
  end

  def pair_photo(from_user:, to_user:)
    # Check generated file in cache folder
    # Heroku allows writing to 'tmp' folder ;)
    blob = nil
    rkey = Rails.root.join("tmp", "photo_#{from_user}_#{to_user}.png")
    dir = Rails.root.join("tmp")
    Dir.mkdir dir if !Dir.exist? dir
    if File.exist? rkey
      f = File.open(rkey,'r')
      blob = f.read
      f.close
    end

    # Generate new blob
    if !blob
      # Attempt to download avatar and store as file
      f_file = Rails.root.join("tmp", "#{from_user}.png")
      t_file = Rails.root.join("tmp", "#{to_user}.png")
      if !File.exist? f_file
        f_user = User.find_by_username(from_user)
        return nil if !f_user || f_user.avatar_url.blank?
        download_avatar(username: f_user.username, image_url: f_user.avatar_url)
      end
      if !File.exist? t_file
        t_user = User.find_by_username(to_user)
        return nil if !t_user || t_user.avatar_url.blank?
        download_avatar(username: t_user.username, image_url: t_user.avatar_url)
      end

      icon = '<img src="data:image/png;base64,"/>'
      html = "<style>body{margin:0;padding:0;overflow:hidden}</style><img src='#{f_file.to_s}'/>#{icon}<img src='#{t_file.to_s}'/>"
      kit = IMGKit.new(html, width: 144, height: 48)
      blob = kit.to_img(:png)
      File.open(rkey, "w+b") do |f|
        f.write(blob)
      end
    end

    return blob
  end

  def slack_encrypt(plain:)
    aes = OpenSSL::Cipher.new("AES-256-CBC")
    aes.encrypt
    aes.key = Digest::SHA2.digest(ENV["SECRET_TOKEN"])
    (aes.update(plain) + aes.final).unpack("H*")[0]
  end

  def slack_decrypt(encoded_str:)
    begin
      aes = OpenSSL::Cipher.new("AES-256-CBC")
      aes.decrypt
      aes.key = Digest::SHA2.digest(ENV["SECRET_TOKEN"])
      aes.update([encoded_str].pack("H*")) + aes.final
    rescue
      return nil
    end
  end

end
